import React, { useState, useEffect } from 'react';
import { Link } from 'react-router';
import styles from '../../Common/Layout/LeftSubSidebar/LeftSubSidebar.scss';
import {
  getFunctionModifyRoute,
  getTableBrowseRoute,
} from '../../Common/utils/routesUtils';
import GqlCompatibilityWarning from '../../Common/GqlCompatibilityWarning/GqlCompatibilityWarning';

type SourceItemsTypes =
  | 'database'
  | 'schema'
  | 'view'
  | 'enum'
  | 'function'
  | 'table';

type SourceItem = {
  name: string;
  type: SourceItemsTypes;
  children?: SourceItem[];
};

const activeStyle = {
  color: '#fd9540',
};

type LeafItemsViewProps = {
  item: SourceItem;
  currentSource: string;
  currentSchema: string;
  setActiveTable: (value: string) => void;
  isActive: boolean;
};
const LeafItemsView: React.FC<LeafItemsViewProps> = ({
  item,
  currentSource,
  currentSchema,
  setActiveTable,
  isActive,
}) => {
  const [isOpen, setIsOpen] = useState(false);

  const isView = item.type === 'view';

  const iconStyle = {
    marginRight: '5px',
    fontSize: '12px',
    width: '12px',
  };

  const activeIcon =
    'data:image/svg+xml;base64,';
  const nonActiveIcon =
    'data:image/svg+xml;base64,';

  return (
    <>
      <div
        onClick={() => {
          setIsOpen(prev => !prev);
          setActiveTable(item.name);
        }}
        onKeyDown={() => {
          setIsOpen(prev => !prev);
          setActiveTable(item.name);
        }}
        role="button"
      >
        <span
          className={
            item.children &&
            `${styles.title} ${isOpen ? '' : styles.titleClosed}`
          }
        >
          {item.type === 'function' ? (
            <Link
              to={getFunctionModifyRoute(
                currentSchema,
                currentSource,
                item.name
              )}
              data-test={item.name}
              style={isActive ? activeStyle : {}}
            >
              <img
                style={iconStyle}
                src={isActive ? activeIcon : nonActiveIcon}
                alt="function icon"
              />
              {item.name}
            </Link>
          ) : (
            <>
              <Link
                to={getTableBrowseRoute(
                  currentSchema,
                  currentSource,
                  item.name,
                  !isView
                )}
                data-test={item.name}
                style={isActive ? activeStyle : {}}
              >
                {item.type === 'enum' ? (
                  <i className="fa fa-list-ul" />
                ) : (
                  <i className="fa fa-table" />
                )}
                {item.type === 'view' ? <i>{item.name}</i> : item.name}
              </Link>
              <GqlCompatibilityWarning
                identifier={item.name}
                className={styles.add_mar_left_mid}
              />
            </>
          )}
        </span>
      </div>
    </>
  );
};

type SchemaItemsViewProps = {
  item: SourceItem;
  currentSource: string;
  isActive: boolean;
  setActiveSchema: (value: string) => void;
};
const SchemaItemsView: React.FC<SchemaItemsViewProps> = ({
  item,
  currentSource,
  isActive,
  setActiveSchema,
}) => {
  const [isOpen, setIsOpen] = useState(false);
  const [activeTable, setActiveTable] = useState<string | null>(null);

  useEffect(() => {
    if (!isActive) {
      setActiveTable(null);
    }
    setIsOpen(isActive);
  }, [isActive]);

  const handleActiveTable = (value: string) => {
    setActiveTable(value);
  };

  return (
    <>
      <div
        onClick={() => {
          setActiveSchema(item.name);
        }}
        onKeyDown={() => {
          setActiveSchema(item.name);
        }}
        role="button"
        className={styles.padd_bottom_small}
        style={isActive ? activeStyle : {}}
      >
        <span
          className={
            item.children &&
            `${styles.title} ${isOpen ? '' : styles.titleClosed}`
          }
        >
          <i className={`${isOpen ? 'fa fa-folder-open' : 'fa fa-folder'}`} />{' '}
          {item.name}
        </span>
      </div>
      <ul className={styles.reducedChildPadding}>
        {isOpen && item.children
          ? item.children.map((child, key) => (
              <li key={key}>
                <LeafItemsView
                  item={child}
                  currentSource={currentSource}
                  currentSchema={item.name}
                  setActiveTable={handleActiveTable}
                  isActive={activeTable === child.name}
                  key={key}
                />
              </li>
            ))
          : null}
      </ul>
    </>
  );
};

type DatabaseItemsViewProps = {
  item: SourceItem;
  isActive: boolean;
  setActiveDataSource: (activeSource: string) => void;
  onSchemaChange: (value: string) => void;
  currentSchema: string;
};
const DatabaseItemsView: React.FC<DatabaseItemsViewProps> = ({
  item,
  isActive,
  setActiveDataSource,
  onSchemaChange,
  currentSchema,
}) => {
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    setIsOpen(isActive);
  }, [isActive]);
  const handleSelectSchema = (value: string) => {
    onSchemaChange(value);
  };

  return (
    <div className={styles.padd_bottom_small}>
      <div
        onClick={() => {
          setIsOpen(prev => !prev);
          setActiveDataSource(item.name);
        }}
        onKeyDown={() => {
          setIsOpen(prev => !prev);
          setActiveDataSource(item.name);
        }}
        role="button"
        className={styles.padd_bottom_small}
      >
        <span
          className={
            item.children &&
            `${styles.title} ${isOpen ? '' : styles.titleClosed}`
          }
        >
          <i className={`fa fa-${item.type}`} /> {item.name}
        </span>
      </div>
      {isOpen && item.children
        ? item.children.map((child, key) => (
            <li key={key}>
              <SchemaItemsView
                item={child}
                currentSource={item.name}
                isActive={child.name === currentSchema}
                setActiveSchema={handleSelectSchema}
                key={key}
              />
            </li>
          ))
        : null}
    </div>
  );
};

type TreeViewProps = {
  items: SourceItem[];
  onDatabaseChange: (value: string) => void;
  onSchemaChange: (value: string) => void;
  currentDataSource: string;
  currentSchema: string;
};
const TreeView: React.FC<TreeViewProps> = ({
  items,
  onDatabaseChange,
  currentDataSource,
  onSchemaChange,
  currentSchema,
}) => {
  const handleSelectDataSource = (dataSource: string) => {
    onDatabaseChange(dataSource);
  };

  if (items.length === 0) {
    return (
      <li className={styles.noChildren} data-test="sidebar-no-services">
        <i>No data available</i>
      </li>
    );
  }

  return (
    <div className={styles.treeNav}>
      {items.map((item, key) => (
        <DatabaseItemsView
          item={item}
          onSchemaChange={onSchemaChange}
          key={key}
          isActive={currentDataSource === item.name}
          setActiveDataSource={handleSelectDataSource}
          currentSchema={currentSchema}
        />
      ))}
    </div>
  );
};

export default TreeView;
