_service.sdl:
extend schema
  @link(url: "https://specs.apollo.dev/federation/v2.0", import: ["@key", "@extends", "@external", "@shareable"])

schema {
  query: Query 
}

type Author@key(fields: "author_id") {
  author_id: Int! 
  first_name: String! 
  last_name: String! 
}

scalar Boolean

scalar Float

scalar ID

scalar Int

type Query {
  AuthorByID: Author 
  _entities: _Entity! 
  _service: _Service! 
}

scalar String

scalar _Any

union _Entity = Author

type _Service {
  sdl: String! 
}
